from bs4 import BeautifulSoupimport requestsr = requests.get('http://shici.chazidian.com/shi1389/')s = BeautifulSoup(r.text, 'html.parser')result = s.find_all('p', attrs={"style":"text-align:center;"}, limit=6)ss = BeautifulSoup(str(result), 'html.parser')result = ss.find_all('span')print(s.title.string[0:3])for i, x in enumerate(result):    if i > 0:        print(x.string.replace(u'\xa0', u' ')+'\n')    else:        print(x.string.replace(u'\xa0', u' ')+'\n\n')    